import React, { useEffect } from 'react';
import socket from '../socket';

function Live() {
  useEffect(() => {
    // Listen for song updates
    socket.on('songUpdate', (data) => {
      console.log('Received song update:', data);
      // TODO: Update your state with the new song data
    });
    
    // Clean up the socket connection on unmount
    return () => {
      socket.off('songUpdate');
    };
  }, []);

  return (
    <div>
      <h2>Live Session</h2>
      {/* Display song name, artist, lyrics, and chords based on user role */}
    </div>
  );
}

export default Live;
